var isPermitNotification = () => {
    if (!('Notification' in window)) {
        console.log('This browser does not support desktop notification');
    } else if (Notification.permission === 'granted') {
        console.log('Notification permission has granted');
    } else if (Notification.permission !== 'denied') {
        Notification.requestPermission().then((permission) => {
            if (permission === 'granted') {
                console.log('Notification permission has reinitialized');
            }
        });
    } else {
        console.log('Please reset your notification permission from browser setting');
    }
};


var sentPushNotification = (text) => {
    if (Notification.permission === "granted") {
        const notification = new Notification('Pilot Bazar', {
            body: text ?? 'asfd',
            icon: 'https://i.ibb.co.com/PZTfqc1/laravel-icon.png',
            lang: 'en',
            vibrate: [200, 100, 200],
        });
        notification.onclick = function (event) {
            event.preventDefault();
            window.open('http://127.0.0.1:8080/chat', '_blank');
        }
    }
}


