var shouldBeScrollToBottomOnSetTimeout = () => {
    let container = document.querySelector('#chatBox');
    if (container) {
        setTimeout(() => {
            container.scrollTo({
                top: container.scrollHeight,
                behavior: 'smooth'
            });
        }, 100)
    }
}


